import { StyleSheet, Text, View } from "react-native";

const  styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 24,
      backgroundColor: "#eaeaea"
    },
  
   area_main:{
       borderStyle:'solid',
       borderWidth:1,
       borderBottomColor:'#d3d3d3',
       borderTopColor:'#fff',
       display:'flex',
       justifyContent:'space-between',
       alignItems:'flex-start'
   }
  });

  export default styles
